mapper.select:
	/* secEduNotiEmailMngRepository.selectSecEduNotiEmailMngView (dmSecEduNotiEmailMng_View) */
  SELECT TITLE,
		 CONTENT, 
		 COMP_ID, 
		 EMAIL_GBN
	FROM SYS_COMP_EMAIL
	WHERE COMP_ID = #{compId}
	  AND EMAIL_GBN= #{emailGbn}

mapper.update:
/* secEduNotiEmailMngRepository.updateSecEduNotiEmailMng (dmSecEduNotiEmailMng_Reg) */
UPDATE SYS_COMP_EMAIL SET	
	  TITLE  = #{title}
	,CONTENT = #{content}
	,MOD_BY  = #{crtBy}
	,MOD_DTM = SYSDATE
WHERE COMP_ID = #{compId}
  AND EMAIL_GBN = #{emailGbn}
