mapper.select:
WITH PAGE AS ( SELECT ROWNUM AS ROW_NUM, T1.* FROM ( SELECT BOARD_NO ,UP_BOARD_NO ,INOUT_GBN ,FN_GET_CODE('Z037',INOUT_GBN,'ETC1') AS INOUT_NM ,CASE WHEN DEPTH > 0 THEN ''|| REPLACE(REPLACE(TITLE, '<', '&lt;'), '>', '&gt;') ELSE REPLACE(REPLACE(TITLE, '<', '&lt;'), '>', '&gt;') END AS TITLE ,CRT_BY ,FN_GET_EMP_NM(CRT_BY) AS CRT_NM ,READ_CNT ,FILE_PATH ,CRT_DTM FROM HP_BOARD WHERE NVL(DEL_YN ,'N') = 'N' AND BOARD_GBN = 'NOTICE'
AND INOUT_GBN = #{inoutGbn}
AND UPPER(TITLE) LIKE '%'|| UPPER(#{searchTitle}) ||'%'
AND UPPER(TITLE) LIKE '%'|| UPPER(#{searchTitle2}) ||'%'
START WITH UP_BOARD_NO = '0' CONNECT BY PRIOR BOARD_NO = UP_BOARD_NO ORDER SIBLINGS BY BOARD_NO DESC ) T1 ) SELECT * FROM PAGE T2 WHERE T2.ROW_NUM BETWEEN ( (#{pageIndex} - 1) * #{pageSize}) + 1 AND ( (#{pageIndex} - 1) * #{pageSize}) + #{pageSize}

mapper.insert:
INSERT INTO HP_BOARD(
    BOARD_NO
  ,INOUT_GBN
  ,BOARD_GBN
  ,EMP_ID
  ,TITLE
  ,CONTENT
  ,FILE_PATH
  ,URL
  ,UP_BOARD_NO
  ,DEPTH
  ,READ_CNT
  ,DEL_YN
  ,AC_IP
  ,CRT_BY
  ,CRT_DTM
  ) VALUES (
    SEQ_HP_BOARD.NEXTVAL
  , #{inoutGbn}
  , 'NOTICE'
  , #{crtBy}
  , #{title}
  , #{content}
  , #{files}
  , #{url}
  , #{upBoardNo}
  , (SELECT  NVL(MAX(DEPTH),0) + 1  FROM HP_BOARD WHERE BOARD_NO = #{upBoardNo} AND NVL(DEL_YN,'N') = 'N')
  , '0'
  , 'N'
  , #{acIp}
  , #{crtBy}
  , SYSDATE)

mapper.update:
UPDATE  HP_BOARD SET
  MOD_BY = #{crtBy}
 ,MOD_DTM = SYSDATE
 ,TITLE = #{title}
 ,CONTENT = #{content}
 ,INOUT_GBN = #{inoutGbn}

  ,FILE_PATH = #{files}

WHERE BOARD_GBN= 'NOTICE'
  AND DEL_YN ='N'
  AND BOARD_NO = #{boardNo}

UPDATE HP_BOARD
SET READ_CNT = READ_CNT + 1
WHERE BOARD_NO = #{boardNo}

UPDATE HP_BOARD
SET DEL_YN='Y'
  , MOD_BY = #{crtBy}
  , MOD_DTM = SYSDATE
WHERE BOARD_NO = #{boardNo}

UPDATE  HP_BOARD SET
  MOD_BY = #{crtBy}
 ,MOD_DTM = SYSDATE
 ,FILE_PATH = #{files}
WHERE BOARD_GBN= 'NOTICE'
  AND BOARD_NO = #{boardNo}
